const initialState = {
  pokemonList: [],
  capturedPokemon: '',
};

const pokemonReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_POKEMON_LIST':
      return { ...state, pokemonList: action.payload };
    case 'CAPTURE_POKEMON':
      return { ...state, capturedPokemon: action.payload };
    default:
      return state;
  }
};

export default pokemonReducer;
