import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDataAction } from "../actions/index.js"; // Import your action(s) here


function App() {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.data); // Assuming "data" is a piece of state in your Redux store


  console.log(data)


  const fetchData = () => {
    const id = Math.floor(Math.random() * 1306) + 1;
    dispatch(fetchDataAction(id));
  };
  // console.log("hello",fetchData())

  return (
    <div className="App">
      <h1>My Pokemon App</h1>
      <button onClick={fetchData}>Fetch Pokemon Data</button>
      {data ? (
        data.sprite ? (
          <div>
            <img src={data.sprite} alt={data.name} />
            <p>{data.name}</p>
            <p>{data.type}</p>
          </div>
        ) : (
          <p>No Pokemon data available</p>
        )
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
