import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchDataAction } from '../redux/actions'; // Import your action(s) here

function App() {
  const dispatch = useDispatch();
  const data = useSelector(state => state.data); // Assuming "data" is a piece of state in your Redux store

  const fetchData = () => {
    // Dispatch the action to fetch data
    dispatch(fetchDataAction());
  };

  return (
    <div className="App">
      <h1>My Pokemon App</h1>
      <button onClick={fetchData}>Fetch Pokemon Data</button>

      {data && (
        <ul>
          {data.map(pokemon => (
            <li key={pokemon.id}>{pokemon.name}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
