import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { setPokemonList, capturePokemon } from './features/actions';

function App() {
  const dispatch = useDispatch();
  const pokemonList = useSelector((state) => state.pokemonList);
  const capturedPokemon = useSelector((state) => state.capturedPokemon);

  useEffect(() => {
    // Fetch Pokemon list from the backend
    axios.get('/api/pokemon')
      .then(response => dispatch(setPokemonList(response.data)))
      .catch(error => console.error('Error fetching Pokemon:', error));
  }, [dispatch]);

  const handleCapturePokemon = async (pokemonName) => {
    // Capture a Pokemon
    try {
      await axios.post('/api/pokemon', { name: pokemonName });
      dispatch(capturePokemon(pokemonName));
    } catch (error) {
      console.error('Error capturing Pokemon:', error);
    }
  };

  return (
    <div className="App">
      <h1>Pokemon App</h1>
      <ul>
        {pokemonList.map((pokemon) => (
          <li key={pokemon._id}>
            {pokemon.name} {!pokemon.captured && <button onClick={() => handleCapturePokemon(pokemon.name)}>Capture</button>}
          </li>
        ))}
      </ul>
      {capturedPokemon && <p>{capturedPokemon} captured!</p>}
    </div>
  );
}

export default App;
