import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { catchPokemonAction } from "../actions/index.js";

// Define the CatchPage component
function CatchPage() {
  const dispatch = useDispatch();
  const isCatching = useSelector((state) => state.isCatching); // Assuming you have a state for catching status

  // Function to handle throwing the pokeball
  const throwPokeball = () => {
    dispatch(catchPokemonAction());
  };

  return (
    <div>
      <h1>Catch Pokemon</h1>
      <button onClick={throwPokeball} disabled={isCatching}>
        {isCatching ? "Catching..." : "Throw Pokeball"}
      </button>
      {isCatching && (
        <p>{catchResult ? "Success! You caught the Pokemon!" : "Oh no! The Pokemon got away!"}</p>
      )}
    </div>
  );
}

export default CatchPage;
