import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { catchPokemonAction, fetchDataAction } from "../actions/index.js";

function CatchPage() {
  const dispatch = useDispatch();
  const isCatching = useSelector((state) => state.catch.isCatching);
  const catchResult = useSelector((state) => state.catch.catchResult);
  const caughtPokemon = useSelector((state) => state.catch.caughtPokemon);

  useEffect(() => {
    // Fetch a random Pokemon when the component mounts
    dispatch(fetchDataAction(getRandomPokemonId()));
  }, [dispatch]);

  const getRandomPokemonId = () => Math.floor(Math.random() * 1000) + 1;

  const throwPokeball = () => {
    dispatch(catchPokemonAction());
  };

  return (
    <div>
      <h1>Catch Pokemon</h1>
      <button onClick={throwPokeball} disabled={isCatching}>
        {isCatching ? "Catching..." : "Throw Pokeball"}
      </button>

      {isCatching && (
        <div>
          {catchResult ? (
            <div>
              <p>Success! You caught the Pokemon!</p>
              {caughtPokemon && (
                <div>
                  <img src={caughtPokemon.sprite} alt={caughtPokemon.name} />
                  <p>Name: {caughtPokemon.name}</p>
                  <p>Type: {caughtPokemon.type.join(", ")}</p>
                </div>
              )}
            </div>
          ) : (
            <p>Oh no! The Pokemon got away!</p>
          )}
        </div>
      )}
    </div>
  );
}

export default CatchPage;
